sys_script_include.script:
var GetTable = Class.create();
// Extend the global.AbstractAjaxProcessor class
GetTable.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{

	getTable: function() {
		var tableRecord = new GlideRecord(this.getParameter("table_name"));

		if (this.getParameter("conditions") != '') {
			tableRecord.addEncodedQuery(this.getParameter("conditions"));
		}

		tableRecord.query();

		var attributes = this.getParameter("attributes").split(",");
		var tableString = "<table>";
		tableString += "<tr>";

		for (var i = 0; i < attributes.length; ++i) {
			tableString += "<td>";
			tableString += attributes[i];
			tableString += "</td>";
		}

		tableString += "</tr>";

		while(tableRecord.next()) {
			tableString += "<tr>";

			for (var j = 0; j < attributes.length; ++j) {
				tableString += "<td>";
				tableString += tableRecord[attributes[j]];
				tableString += "</td>";
			}

			tableString += "</tr>";
		}

		tableString += "</table>";

		return tableString;
	},
	type: 'GetTable'
});
